import React, { Fragment, useContext, useEffect } from "react";
import { Segment, Header, Form, Button, Comment, TextArea } from "semantic-ui-react";
import { RootStoreContext } from "../../../app/stores/rootStore";
import { Link } from "react-router-dom";

import {Form as FinalForm,Field} from 'react-final-form';
import TextAreaInput from "../../../app/common/form/TextAreaInput";


const ActivityDetailedChat = () => {
  const rootStore = useContext(RootStoreContext);
  const {
    createHubConnection,
    stopHubConnection,
    addComment,
    activity,
  } = rootStore.activityStore;

  useEffect(() => {
    createHubConnection();
    return () => {
      stopHubConnection();
    };
  }, [createHubConnection, stopHubConnection]);

  return (
    <Fragment>
      <Segment
        textAlign="center"
        attached="top"
        inverted
        color="teal"
        style={{ border: "none" }}
      >
        <Header>Chat about this event</Header>
      </Segment>
      <Segment attached>
        <Comment.Group>
          {activity &&
            activity.comments &&
            activity.comments.map((comment) => (
              <Comment key = {comment.id}>
                <Comment.Avatar src={comment.image || "/assets/user.png"} />
                <Comment.Content>
                  <Comment.Author as={Link} to={`/profile/${comment.username}`}>{comment.display} </Comment.Author>
                  <Comment.Metadata>
            <div>{comment.createAt}</div>
                  </Comment.Metadata>
                  <Comment.Text>{comment.body}</Comment.Text>

                </Comment.Content>
              </Comment>
            ))}
        <FinalForm
        onSubmit={addComment}
        render={({handleSubmit,submitting,form}) => (

          <Form onSubmit={()=> handleSubmit()!.then(() => form.reset())}>
          <Field
          name='body'
          component={TextAreaInput}
          rows={2}
          placeholder='Add your Comment'
          />
          <Button
            content="Add Reply"
            labelPosition="left"
            icon="edit"
            primary
            loading={submitting}
          />
        </Form>
        )}
        />

        </Comment.Group>
      </Segment>
    </Fragment>
  );
};

export default ActivityDetailedChat;
